package com.bonc.common.utils.pptdata;

import javax.crypto.Cipher;
import javax.crypto.spec.IvParameterSpec;
import javax.crypto.spec.SecretKeySpec;
import java.util.Base64;

public class FixedAESDecryptor {

    private static final String KEY = "1234567890abcdef"; // 16字节 key
    private static final String IV = "abcdef1234567890";  // 16字节 iv

    public static String decrypt(String base64CipherText) throws Exception {
        byte[] cipherData = Base64.getDecoder().decode(base64CipherText);
        SecretKeySpec keySpec = new SecretKeySpec(KEY.getBytes("UTF-8"), "AES");
        IvParameterSpec ivSpec = new IvParameterSpec(IV.getBytes("UTF-8"));

        Cipher cipher = Cipher.getInstance("AES/CBC/PKCS5Padding");
        cipher.init(Cipher.DECRYPT_MODE, keySpec, ivSpec);
        byte[] decrypted = cipher.doFinal(cipherData);

        return new String(decrypted, "UTF-8");
    }

    public static void main(String[] args) {
        try {
            String a = "";
            String decrypt = decrypt(a);
            System.out.println("解密后："+decrypt);
        }catch (Exception e){
            e.printStackTrace();
        }

    }
}